        /* Basic reset */
       
        /* Form container */
        .form-container {
            max-width: 1200px;
            margin: 0 auto;
            background: #fff;
            padding: 20px;
            border-radius: 8px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        }
 
        /* Form styles */
        #forma {
            display: grid;
            grid-template-columns: repeat(5, 1fr);
            gap: 20px;
        }

        /* Form fields */
        label {
            font-weight: bold;
            display: block;
            margin-bottom: 8px;
        }

        input,
        select,
        textarea {
            width: 100%;
            padding: 10px;
            border: 1px solid #ccc;
            border-radius: 4px;
            font-size: 16px;
        }

        /* Submit button */
        .submit-btn {
            grid-column: span 5;
            padding: 10px 20px;
            background-color: #4CAF50;
            color: white;
            border: none;
            border-radius: 4px;
            cursor: pointer;
            font-size: 16px;
        }

        .submit-btn:hover {
            background-color: #45a049;
        }

        /* Responsive design for small screens */
        @media (max-width: 768px) {
            #forma {
                grid-template-columns: 1fr; /* Stacks the columns into one */
            }

            .submit-btn {
                grid-column: span 1; /* Makes the button fit the single column */
            }
        }

        @media (max-width: 480px) {
            body {
                padding: 10px;
            }
            .form-container {
                padding: 10px;
            }
        }
