               <script>
        // JavaScript for automatic testimonial sliding
        let currentIndex = 0;
        const testimonials = document.querySelector('.testimonial-slide');
        const totalTestimonials = document.querySelectorAll('.testimonial-item').length;

        // Function to slide testimonials automatically
        function changeTestimonial() {
            currentIndex++;
            if (currentIndex >= totalTestimonials) {
                currentIndex = 0; // Loop back to the first testimonial
            }

            // Shift the slides by adjusting the transform property
            testimonials.style.transform = `translateX(-${currentIndex * 100}%)`;
        }

        // Set interval to automatically change testimonials every 5 seconds (5000 ms)
        setInterval(changeTestimonial, 5000);

    </script>
         <script>
    // JavaScript validation for PDF file type
    document.getElementById("pdfUploadForm").addEventListener("submit", function(event) {
        const pdfFileInput = document.getElementById("pdfFile");
        const errorMessage = document.getElementById("errorMessage");

        // Check if file is selected and is a PDF
        if (pdfFileInput.files.length === 0 || pdfFileInput.files[0].type !== "application/pdf") {
            event.preventDefault(); // Prevent form submission
            errorMessage.style.display = "block";
            errorMessage.innerText = "Please select a valid PDF file.";
        } else {
            errorMessage.style.display = "none"; // Hide error message if file is valid
        }
    });
</script>   
